mapper.delete:
DELETE FROM sys_role_permission
WHERE role_id = #{roleId}
  AND permission_id = #{permissionId}

mapper.select:
SELECT COUNT(*)
FROM sys_role_permission
WHERE role_id = #{roleId}
  AND permission_id = #{permissionId}

SELECT EXISTS(
    SELECT 1
    FROM sys_role_permission
    WHERE role_id = #{roleId}
      AND permission_id = #{permissionId}
    LIMIT 1
)

mapper.delete:
DELETE FROM sys_role_permission
WHERE role_id = #{roleId}
  AND permission_id IN

      #{pid}
