mapper.delete:
DELETE FROM sys_user_role
WHERE user_id = #{userId}
  AND role_id = #{roleId}

mapper.select:
SELECT COUNT(*)
FROM sys_user_role
WHERE user_id = #{userId}
  AND role_id = #{roleId}

SELECT EXISTS(
    SELECT 1
    FROM sys_user_role
    WHERE user_id = #{userId}
      AND role_id = #{roleId}
    LIMIT 1
)

